package Nisikant;

 class Hi {
	public void show() {
		for (int i=0; i<5; i++) {
			System.out.println("Hi");
			
		}
	}}
class Hello{
	public void show() {
		for(int i=0; i<5;i++) {
			System.out.println("Hello");
		}
	}}
	public class withoutthread {
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Hi obj = new Hi();
		Hello obj1 = new Hello();
		obj.show();
		obj1.show();
		
		
		
		
		 
	}

	}
